        $(document).ready(function() {
         
            $('#btnSubmit_L').click(function(){
               var username = $('#CustomerID_L').val();
               var password = $('#Password_L').val();
               var validcode = $('#ValidCode_L').val();
                //检查类型
               var tips = '';
               if(username == '')
                   tips = '用户名不能为空';
               else if( password == '')
                   tips = '密码不能为空';
               else if(validcode == '')
                   tips = '请输入验证码';
               if(tips != '')
                {
                    $('#login_error').html(tips);
                    return;
                }
              $('#login_error').html('');
                $.post( '/index.php?controller=simple&action=ajax_login_act', 
                        {
                            'login_info': username,
                            'password':password,
                            'captcha' : validcode
                        },
                        function(msg){
                            if(msg.code)
                            {
                                $('#login_error').html(msg.message);
                            }
                            else{
                               window.location.reload(); 
                            }

                        },'json');

                    });
                });
        
        
    </script>



































